import {Editor, Element, Frame} from '@craftjs/core';
import {
	Container as MuiContainer,
	Typography,
	makeStyles,
} from '@material-ui/core';
import {Container} from '../../components/selectors/Container';
import Divider from '../../components/selectors/Divider';
import {CoverImage, Image} from '../../components/user/image/image';
import TitleInput from '../../components/user/text/TitleInput';
import {Video} from '../../components/user/video/video';
import NavBar from '../../layouts/editor/appbar';
import MiniDrawer from '../../layouts/editor/drawer';
import React, {useState} from 'react';
import RenderNode from '../../components/user/RenderNode';
import {Text} from '../../components/user/text/Text';
import lz from 'lzutf8';
import {IBlog} from '../../features/editor';

const useStyles = makeStyles({
	main: {
		boxShadow:
			'0 8px 60px 0 rgb(103 151 255 / 11%), 0 12px 90px 0 rgb(103 151 255 / 11%)',
	},
	title: {
		marginTop: '50px',
		marginBottom: '70px',
		paddingTop: '10px',
		paddingBottom: '10px',
	},
	author: {
		fontSize: '1.1em',
		maxWidth: '850px',
		color: '#6d7c90',
		marginTop: '40px',
	},
	coverImage: {
		marginTop: '20px',
	},
});

const EditorPage: React.FC<{edit: boolean; blog?: IBlog}> = ({edit, blog}) => {
	const resolvers = {Text, Image, Video, Divider, Container};
	const classes = useStyles();

	const hashed = `
`;
	console.log(blog?.blogHash === hashed);

	const json = lz.decompress(lz.decodeBase64(blog?.blogHash as string));

	const [values, setValues] = useState<IBlog>({
		id: blog?.id ? blog.id : '',
		title: blog?.title as string,
		coverImageUrl: blog?.coverImageUrl as string,
		blogHash: blog?.blogHash as string,
		authorId: 'jhjhejkhejwehewkhewe',
		date:
			new Date(blog?.date as string).toDateString() ||
			new Date().toDateString(),
	});

	const handleChange = (key: string, value: string) => {
		setValues({
			...values,
			[key]: value,
		});
	};

	console.log('Editor', blog, edit);

	const [enabled, setEnabled] = useState(edit === undefined ? true : edit);
	return (
		<Editor resolver={resolvers} onRender={RenderNode}>
			<NavBar
				enabled={enabled}
				setEnable={setEnabled}
				handleChange={handleChange}
				values={values}
			/>
			<MiniDrawer />
			<MuiContainer maxWidth={'lg'}>
				<div className={classes.title}>
					<Typography variant="h1" align="center">
						{enabled && (
							<TitleInput value={values.title} handleChange={handleChange} />
						)}
						{!enabled && values.title}
					</Typography>
					<Typography variant="body1" align="center" className={classes.author}>
						Posted on Posted on {values.date} by B.J. Keeton
					</Typography>
				</div>
			</MuiContainer>

			<MuiContainer maxWidth={'lg'} className={classes.main} disableGutters>
				<div className={classes.coverImage}>
					<CoverImage
						handleChange={handleChange}
						imageUrl={blog?.coverImageUrl}
					/>
				</div>

				<Frame data={json}>
					<Element
						canvas
						is={Container}
						width="100%"
						height="auto"
						flexDirection="column"
						padding={['30', '30', '30', '30']}>
						<Element
							canvas
							is={Container}
							flexDirection="row"
							width="auto"
							height="auto"
							padding={['30', '30', '30', '30']}
							margin={['0', '0', '40', '0']}>
							<Element
								canvas
								is={Container}
								width="40%"
								height="100%"
								padding={['0', '20', '0', '20']}>
								<Text
									fontSize="23"
									text="Craft.js is a React framework for building powerful &amp; feature-rich drag-n-drop page editors."></Text>
							</Element>
							<Element
								canvas
								is={Container}
								width="60%"
								height="100%"
								padding={['0', '20', '0', '20']}>
								<Text
									fontSize="14"
									text="Everything you see here, including the editor, itself is
										 made of React components. 
										Craft.js comes only with the building blocks for a page editor;
										 it provides a drag-n-drop system and handles the way user 
										 components should be rendered, updated and moved, among other things. 
										 <br /> <br /> You control the way your editor looks and behave."></Text>
							</Element>
						</Element>
					</Element>
				</Frame>

				{/* <Grid container spacing={5}>
					<Grid item xs={12} md={12}>
						</Grid>
					<Grid item xs={12} md={2}>
						<Layers expandRootOnLoad={true} />
					</Grid>
				</Grid> */}
			</MuiContainer>
		</Editor>
	);
};

export default EditorPage;
